import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { useGames } from "../contexts/GamesContext";

const GameEdit = () => {
  const { games, isLoading, error } = useGames();
  const { gameId } = useParams();

  if (isLoading) return <div>Loading game data...</div>;
  if (error) return <div>Error: {error}</div>;

  const game = games.find((g) => g.id === Number(gameId));

  const [gameName, setGameName] = useState(game.name);
  const [price, setPrice] = useState(game.price);
  const [releaseDate, setReleaseDate] = useState(game.releaseDate);
  const [genre, setGenre] = useState(game.genre);

  const genres = [
    { id: 1, name: "Fighting" },
    { id: 2, name: "Roleplaying" },
    { id: 3, name: "Sports" },
    { id: 4, name: "Racing" },
    { id: 5, name: "Kids and Family" },
  ];

  const handleChange = (e) => {
    // const { name, value } = e.target;
    // setGame({
    //   ...game,
    //   [name]: value,
    // });
  };

  const handleSubmit = (e) => {
    // e.preventDefault();
    // console.log("Game to save:", game);
    // // Add your save logic here
    // alert("Game saved!");
    // navigate("/games");
  };

  const handleCancel = () => {
    // if (window.confirm("Cancel without saving?")) {
    //   navigate("/games");
    // }
  };

  return (
    <div style={{ padding: "20px", maxWidth: "600px", margin: "0 auto" }}>
      <h2>Edit Game</h2>

      <form
        onSubmit={handleSubmit}
        style={{ display: "flex", flexDirection: "column", gap: "15px" }}
      >
        <div>
          <label
            style={{
              display: "block",
              marginBottom: "5px",
              fontWeight: "bold",
            }}
          >
            Game Name *
          </label>
          <input
            type="text"
            name="name"
            value={gameName}
            onChange={(e) => setGameName(e.target.value)}
            required
            style={{
              width: "100%",
              padding: "10px",
              border: "1px solid #ccc",
              borderRadius: "4px",
            }}
          />
        </div>

        <div>
          <label
            style={{
              display: "block",
              marginBottom: "5px",
              fontWeight: "bold",
            }}
          >
            Price ($) *
          </label>
          <input
            type="number"
            name="price"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
            min="0"
            step="0.01"
            style={{
              width: "100%",
              padding: "10px",
              border: "1px solid #ccc",
              borderRadius: "4px",
            }}
          />
        </div>

        <div>
          <label
            style={{
              display: "block",
              marginBottom: "5px",
              fontWeight: "bold",
            }}
          >
            Release Date *
          </label>
          <input
            type="date"
            name="releaseDate"
            value={releaseDate}
            onChange={(e) => setReleaseDate(e.target.value)}
            required
            style={{
              width: "100%",
              padding: "10px",
              border: "1px solid #ccc",
              borderRadius: "4px",
            }}
          />
        </div>

        <div>
          <label
            style={{
              display: "block",
              marginBottom: "5px",
              fontWeight: "bold",
            }}
          >
            Genre *
          </label>
          <select
            name="genreId"
            value={genre}
            onChange={(e) => setGenre(e.target.value)}
            required
            style={{
              width: "100%",
              padding: "10px",
              border: "1px solid #ccc",
              borderRadius: "4px",
            }}
          >
            <option>{game.genre}</option>
            {genres.map((genre) => (
              <option key={genre.id} value={genre.id}>
                {genre.name}
              </option>
            ))}
          </select>
        </div>

        <div style={{ display: "flex", gap: "10px", marginTop: "20px" }}>
          <button
            type="submit"
            style={{
              padding: "10px 20px",
              backgroundColor: "#4CAF50",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
              flex: 1,
            }}
          >
            Save Game
          </button>

          <button
            type="button"
            onClick={handleCancel}
            style={{
              padding: "10px 20px",
              backgroundColor: "#f44336",
              color: "white",
              border: "none",
              borderRadius: "4px",
              cursor: "pointer",
              flex: 1,
            }}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default GameEdit;
