import React from "react";
import "../index.css";
import { useNavigate } from "react-router-dom";
import { useGames } from "../contexts/GamesContext";

const Main = () => {
  const navigate = useNavigate();
  const { games, deleteGame } = useGames();
  console.log(games);

  // const games = [
  //   {
  //     id: 1,
  //     name: "The Legend of Zelda: Breath of the Wild",
  //     price: "$59.99",
  //     releaseDate: "2017-03-03",
  //     genre: "Action-Adventure",
  //     platform: "Nintendo Switch",
  //   },
  //   {
  //     id: 2,
  //     name: "Cyberpunk 2077",
  //     price: "$49.99",
  //     releaseDate: "2020-12-10",
  //     genre: "RPG",
  //     platform: "PC/PS5/Xbox",
  //   },
  //   {
  //     id: 3,
  //     name: "Elden Ring",
  //     price: "$59.99",
  //     releaseDate: "2022-02-25",
  //     genre: "Action RPG",
  //     platform: "Multi-platform",
  //   },
  //   {
  //     id: 4,
  //     name: "God of War: Ragnar√∂k",
  //     price: "$69.99",
  //     releaseDate: "2022-11-09",
  //     genre: "Action-Adventure",
  //     platform: "PS5/PS4",
  //   },
  //   {
  //     id: 5,
  //     name: "Minecraft",
  //     price: "$26.99",
  //     releaseDate: "2011-11-18",
  //     genre: "Sandbox",
  //     platform: "Multi-platform",
  //   },
  //   {
  //     id: 6,
  //     name: "Grand Theft Auto V",
  //     price: "$39.99",
  //     releaseDate: "2013-09-17",
  //     genre: "Action-Adventure",
  //     platform: "Multi-platform",
  //   },
  //   {
  //     id: 7,
  //     name: "Red Dead Redemption 2",
  //     price: "$59.99",
  //     releaseDate: "2018-10-26",
  //     genre: "Action-Adventure",
  //     platform: "Multi-platform",
  //   },
  //   {
  //     id: 8,
  //     name: "The Witcher 3: Wild Hunt",
  //     price: "$39.99",
  //     releaseDate: "2015-05-19",
  //     genre: "Action RPG",
  //     platform: "Multi-platform",
  //   },
  // ];

  // Format date to be more readable
  const formatDate = (dateString) => {
    const options = { year: "numeric", month: "short", day: "numeric" };
    return new Date(dateString).toLocaleDateString("en-US", options);
  };

  function handleDeleteGame(id) {
    deleteGame(id);
  }

  return (
    <main className="main-container">
      <div className="games-section">
        <div className="section-header">
          <h2>üéÆ Your Games List</h2>
        </div>

        <div className="table-footer">
          <p>Showing {games.length} games</p>
          <div className="sort-options">
            <span>Sort by:</span>
            <select className="sort-select">
              <option value="name">Name</option>
              <option value="price">Price</option>
              <option value="date">Release Date</option>
              <option value="genre">Genre</option>
            </select>
          </div>
        </div>

        <div className="table-container">
          <table className="games-table">
            <thead>
              <tr>
                <th>#</th>
                <th>Name</th>
                <th>Price</th>
                <th>Release Date</th>
                <th>Genre</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {games.map((game, index) => (
                <tr key={game.id}>
                  <td className="index-column">{index + 1}</td>
                  <td className="game-name">
                    <div className="game-name-content">
                      <span className="game-icon">üéØ</span>
                      <span>{game.name}</span>
                    </div>
                  </td>
                  <td className="price-column">
                    <span className="price-tag">{game.price}</span>
                  </td>
                  <td className="date-column">
                    {formatDate(game.releaseDate)}
                  </td>
                  <td className="genre-column">
                    <span className="genre-badge">{game.genre}</span>
                  </td>
                  <td className="actions-column">
                    <button
                      className="btn-view"
                      onClick={() => navigate(`games/${game.id}`)}
                    >
                      Edit
                    </button>
                    <button className="btn-view" onClick={handleDeleteGame}>
                      ‚ùå
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </main>
  );
};

export default Main;
