import {
  createContext,
  useCallback,
  useContext,
  useEffect,
  useState,
} from "react";

const GamesContext = createContext();

const BASE_URL = "http://localhost:5042";

function GamesProvider({ children }) {
  const [games, setGames] = useState([]);
  const [currentGame, setCurrentGame] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const genres = [
    { id: 1, name: "Fighting" },
    { id: 2, name: "Roleplaying" },
    { id: 3, name: "Sports" },
    { id: 4, name: "Racing" },
    { id: 5, name: "Kids and Family" },
  ];

  useEffect(function () {
    async function fetchGames() {
      try {
        setIsLoading(true);
        setError(null);
        const res = await fetch(`${BASE_URL}/games`);
        if (!res.ok) {
          throw new Error(`Failed to fetch games: ${res.status}`);
        }
        const data = await res.json();
        setGames(data);
      } catch (err) {
        console.log(err);
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    }
    fetchGames();
  }, []);

  const getGame = useCallback(
    async function getGame(id) {
      if (Number(id) === currentGame.id) return;
      try {
        setIsLoading(true);
        setError(null);
        const res = await fetch(`${BASE_URL}/games/${id}`);
        const data = await res.json();
        setCurrentGame(data);
      } catch (err) {
        console.log(err);
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    },
    [currentGame.id]
  );

  async function editGame(game) {
    try {
      setIsLoading(true);
      setError(null);

      if (!game || !game.id) {
        throw new Error("Game ID is required for editing");
      }

      const res = await fetch(`${BASE_URL}/games/${game.id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(game),
      });

      if (!res.ok) {
        const errorText = await res.text();
        console.error("âŒ Server error response:", errorText);
        throw new Error(
          `Failed to update game: ${res.status} ${res.statusText} - ${errorText}`
        );
      }

      const gameGenre = genres.find((genre) => genre.id === game.genreId);
      const genreName = gameGenre ? gameGenre.name : "Unknown Genre";

