      setGames((prevGames) =>
        prevGames.map((g) => (g.id === game.id ? game : g))
      );

      return { success: true };
    } catch (err) {
      console.error("ðŸ’¥ Error updating game:", err);
      setError(err.message);
      return { success: false, error: err.message };
    } finally {
      setIsLoading(false);
    }
  }

  // async function editGame(game) {
  //   try {
  //     setIsLoading(true);
  //     setError(null);
  //     if (!game || !game.id) {
  //       throw new Error("Game ID is required for editing");
  //     }

  //     const res = await fetch(`${BASE_URL}/games/${game.id}`, {
  //       method: "PUT",
  //       headers: {
  //         "Content-Type": "application/json",
  //       },
  //       body: JSON.stringify(game),
  //     });

  //     if (!res.ok) {
  //       throw new Error(
  //         `Failed to update game: ${res.status} ${res.statusText}`
  //       );
  //     }

  //     const updatedGame = await res.json();

  //     setGames((prevGames) =>
  //       prevGames.map((game) =>
  //         game.id === updatedGame.id ? updatedGame : game
  //       )
  //     );
  //   } catch (err) {
  //     console.error("Error updating game:", err);
  //     setError(err.message);
  //     return { success: false, error: err.message };
  //   } finally {
  //     setIsLoading(false);
  //   }
  // }

  return (
    <GamesContext.Provider
      value={{
        games,
        getGame,
        editGame,
        currentGame,
        isLoading,
        error,
      }}
    >
      {children}
    </GamesContext.Provider>
  );
}

function useGames() {
  const context = useContext(GamesContext);
  if (context === undefined)
    throw new Error("CitiesContext was used outside of the Cities provider");
  return context;
}

