import { BrowserRouter, Route, Routes } from "react-router-dom";
import HomePage from "./pages/HomePage";
import PageNotFound from "./pages/PageNotFound";
import GameEdit from "./components/GameEdit";
import { useEffect, useState } from "react";

const BASE_URL = "http://localhost:5042";

function App() {
  const [games, setGames] = useState([]);

  useEffect(function () {
    async function fetchGames() {
      try {
        const res = await fetch(`${BASE_URL}/games`);
        const data = await res.json();
        setGames(data);
      } catch (err) {
        console.log(err);
      }
    }
    fetchGames();
  }, []);

  return (
    <BrowserRouter>
      <Routes>
        <Route index element={<HomePage games={games} />} />
        <Route path="games/:id" element={<GameEdit games={games} />} />
        <Route path="/privacy-policy" element={<PageNotFound id={0} />} />
        <Route path="/terms-of-services" element={<PageNotFound id={0} />} />
        <Route path="/contact-us" element={<PageNotFound id={0} />} />
        <Route path="*" element={<PageNotFound id={1} />} />
      </Routes>
    </BrowserRouter>
  );
}

export default App;
