import {
  createContext,
  useCallback,
  useContext,
  useEffect,
  useState,
} from "react";

const GamesContext = createContext();

const BASE_URL = "http://localhost:5042";

function GamesProvider({ children }) {
  const [games, setGames] = useState([]);
  const [currentGame, setCurrentGame] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(function () {
    async function fetchGames() {
      try {
        setIsLoading(true);
        setError(null);
        const res = await fetch(`${BASE_URL}/games`);
        if (!res.ok) {
          throw new Error(`Failed to fetch games: ${res.status}`);
        }
        const data = await res.json();
        setGames(data);
      } catch (err) {
        console.log(err);
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    }
    fetchGames();
  }, []);

  const getGame = useCallback(
    async function getGame(id) {
      if (Number(id) === currentGame.id) return;
      try {
        setIsLoading(true);
        setError(null);
        const res = await fetch(`${BASE_URL}/games/${id}`);
        const data = await res.json();
        setCurrentGame(data);
      } catch (err) {
        console.log(err);
        setError(err.message);
      } finally {
        setIsLoading(false);
      }
    },
    [currentGame.id]
  );

  async function editGame(game) {
    console.log("ðŸš€ editGame called with:", game);
    console.log("ðŸ“Œ Game ID:", game?.id);

    try {
      setIsLoading(true);
      setError(null);

      if (!game || !game.id) {
        console.error("âŒ Missing game ID!");
        throw new Error("Game ID is required for editing");
      }

      console.log(`ðŸŒ Sending PUT to: ${BASE_URL}/games/${game.id}`);

      const res = await fetch(`${BASE_URL}/games/${game.id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(game),
      });

      console.log("ðŸ“¡ Response status:", res.status, res.statusText);

      if (!res.ok) {
        const errorText = await res.text();
        console.error("âŒ Server error response:", errorText);
        throw new Error(
          `Failed to update game: ${res.status} ${res.statusText} - ${errorText}`
        );
      }

      // const updatedGame = await res.json();
      // console.log("âœ… Update successful:", updatedGame);

      setGames((prevGames) =>
        prevGames.map((g) => (g.id === game.id ? game : g))
      );

      return { success: true };
    } catch (err) {
      console.error("ðŸ’¥ Error updating game:", err);
      setError(err.message);
      return { success: false, error: err.message };
    } finally {
      setIsLoading(false);
    }
  }

  // async function editGame(game) {
  //   try {
  //     setIsLoading(true);
  //     setError(null);
  //     if (!game || !game.id) {
  //       throw new Error("Game ID is required for editing");
  //     }

  //     const res = await fetch(`${BASE_URL}/games/${game.id}`, {
  //       method: "PUT",
  //       headers: {
  //         "Content-Type": "application/json",
  //       },
  //       body: JSON.stringify(game),
  //     });

  //     if (!res.ok) {
  //       throw new Error(
  //         `Failed to update game: ${res.status} ${res.statusText}`
  //       );
  //     }

  //     const updatedGame = await res.json();

  //     setGames((prevGames) =>
  //       prevGames.map((game) =>
  //         game.id === updatedGame.id ? updatedGame : game
  //       )
  //     );
  //   } catch (err) {
  //     console.error("Error updating game:", err);
  //     setError(err.message);
  //     return { success: false, error: err.message };
  //   } finally {
  //     setIsLoading(false);
  //   }
  // }

  return (
    <GamesContext.Provider
      value={{
        games,
        getGame,
        editGame,
        currentGame,
        isLoading,
        error,
      }}
    >
      {children}
    </GamesContext.Provider>
  );
}

function useGames() {
  const context = useContext(GamesContext);
  if (context === undefined)
    throw new Error("CitiesContext was used outside of the Cities provider");
  return context;
}

export { GamesProvider, useGames };
